import "../App.css"
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";
import recipes, { Recipe } from "../recipeData";

export default function RecipePreview(props: Recipe) {
    const [fetRecipes, setFetRecipes] = useState<Recipe[]>([])
    useEffect(() => {
      fetch("https://bootcamp-milestone-4.onrender.com/recipe")
      .then((response) => response.json())
      .then((data) => setFetRecipes(data))
    }, [])

    let all = [...recipes, ...fetRecipes];

    return (
        <div className="recipe">
            <h2>
                <Link to={"/recipes/" + props.name}>
                    {props.name}
                </Link>
            </h2>
            <div className="recipe-body">
                <img className="img-preview" src={props.image} alt="food pic"/>
                <p className="decription">
                    {props.description}
                </p>
            </div>
        </div>
    )
}
