import { useParams } from "react-router-dom";
import { Recipe } from "../recipeData";
import recipes from '../recipeData';

// milestone 3.5
import React, { ChangeEvent, useState } from 'react';
import { useEffect } from "react";

export default function RecipePage(props: Recipe) {
    // const [fetRecipes, setFetRecipes] = useState<Recipe[]>([])
    // useEffect(() => {
    // fetch("https://bootcamp-milestone-4.onrender.com/recipe")
    // .then((response) => response.json())
    // .then((data) => setFetRecipes(data))
    // }, [])

    // let all = [...recipes, ...fetRecipes];

    let { param } = useParams();
    let curr: Recipe | undefined = recipes.find((recipe) => recipe.path === param);

    // new state
    const [allIngredients, setAllIngredients] = useState(props.ingredients);
    const [newIngredient, setNewIngredient] = useState('');
    const [allInstructions, setAllInstructions] = useState(props.instructions);
    const [newInstruction, setNewInstruction] = useState('');

    return (
        <main className="recipe-page">
            <div className="intro">
                <img className="intro-image" src={curr?.image2} alt="food pic"/>
                <h1 className="intro-title">{curr?.name}</h1>
                <div className="intro-info">
                    {curr?.info.map((i) => (<p>{i}</p>))}
                    <br></br>
                    {curr?.intro}
                </div>
            </div>
            <div className="flex-container">
                <div className="flex-content">
                    <h2 className="label">Ingredients</h2>
                    <ul>
                        {curr?.ingredients.map((i) => (<li>{i}</li>))}
                    </ul>
                        <p>Add ingredient: </p>
                        <input placeholder="2 cups of flour"
                            value={newIngredient}
                            onChange={(e: ChangeEvent<HTMLInputElement>) => {
                                setNewIngredient(e.target.value); }}
                        />
                        <button onClick={() => { setAllIngredients([...allIngredients, newIngredient]);}}>Add Ingredient</button>
                        <h2 className="label">Instructions</h2>
                        <ol>
                            {curr?.instructions.map((instr) => (<li>{instr}</li>))}
                        </ol>
                        <p>Add an instruction: </p>
                        <input placeholder="Beat the eggs"
                            value={newInstruction}
                            onChange={(e: ChangeEvent<HTMLInputElement>) => {
                                setNewInstruction(e.target.value); }}
                        />
                        <button onClick={() => { setAllInstructions([...allInstructions, newInstruction]);}}>Add Instruction</button>
                </div>
            </div>
        </main>
    );
}
