import { useParams } from "react-router-dom";
import recipes, { Recipe } from "../recipeData";

// milestone 3.5
import React, { ChangeEvent, useState, useEffect } from 'react';

interface RecipeProp {
    external: boolean;
}

export default function RecipePage(props: RecipeProp) {
    // const [fetRecipes, setFetRecipes] = useState<Recipe[]>([])
    // useEffect(() => {
    // fetch("https://bootcamp-milestone-4.onrender.com/recipe")
    // .then((response) => response.json())
    // .then((data) => setFetRecipes(data))
    // }, [])

    // let all = [...recipes, ...fetRecipes];

    // let { param } = useParams();
    // let curr = recipes.find((recipe) => recipe.path === param);

    const [ingredients, setIngredients] = useState<any[]>([]);
    const [newIngredient, setNewIngredient] = useState('');

    const [instructions, setInstructions] = useState<any[]>([]);
    const [newInstruction, setNewInstruction] = useState('');

    const [curr, setCurrRecipe] = useState<Recipe>(recipes[0]);

    let { param } = useParams();

    useEffect(() => {
        if (props.external) {
            const getExt = async() => {
                let data = await fetch("https://bootcamp-milestone-4.onrender.com/recipe/" + param);
                let recipeData = await data.json();
                setCurrRecipe({...recipeData[0], image: recipeData[0].image});
                setIngredients([...recipeData[0].ingredients]);
                setInstructions([...recipeData[0].instructions]);
            }
            getExt();
            console.log(curr);
        } else {
            let target = recipes.find(element => (element.name === param))!;
            setCurrRecipe(target)
            setIngredients([...target.ingredients])
            setInstructions([...target.instructions])
        }
    }, [param, props.external]);


    return (
        <main className="recipe-page">
            <div className="intro">
                <img className="intro-image" src={curr.image2} alt={curr.name}/>
                <h1 className="intro-title">{curr.name}</h1>
                <div className="intro-info">
                    {curr.info.map((i) => (<p>{i}</p>))}
                    <br></br>
                    {curr.intro}
                </div>
            </div>
            <div className="flex-container">
                <div className="flex-content">
                    <h2 className="label">Ingredients</h2>
                    <ul>
                        {curr.ingredients.map((i) => (<li>{i}</li>))}
                    </ul>
                    <div className="add-values">
                        <p>Add ingredient: </p>
                        <input placeholder="2 cups of flour"
                            value={newIngredient}
                            onChange={(e: ChangeEvent<HTMLInputElement>) => {
                                setNewIngredient(e.target.value); console.log(newIngredient);}}
                        />
                        <button onClick={() => { setIngredients([...ingredients, newIngredient]); }}>Add Ingredient</button>
                    </div>
                    <h2 className="label">Instructions</h2>
                    <ol>
                        {curr.instructions.map((instr) => (<li>{instr}</li>))}
                    </ol>
                    <div className="add-values">
                        <p>Add an instruction: </p>
                        <input placeholder="Beat the eggs"
                            value={newInstruction}
                            onChange={(e: ChangeEvent<HTMLInputElement>) => {
                                setNewInstruction(e.target.value); }}
                        />
                        <button onClick={() => { setInstructions([...instructions, newInstruction]);}}>Add Instruction</button>
                    </div>
                </div>
            </div>
        </main>
    );
}
