import { useParams } from "react-router-dom";
import recipes, { Recipe } from "../recipeData";

// milestone 3.5
import React, { ChangeEvent, useState } from 'react';
import { useEffect } from "react";

export default function RecipePage(props: Recipe) {
    const [fetRecipes, setFetRecipes] = useState<Recipe[]>([])
    useEffect(() => {
    fetch("https://bootcamp-milestone-4.onrender.com/recipe")
    .then((response) => response.json())
    .then((data) => setFetRecipes(data))
    }, [])

    let myRecipes = [...recipes];
    let externalRecipes = [...fetRecipes];

    let { param } = useParams();
    let curr1 = myRecipes.find((recipe) => recipe.name === param);
    let curr2 = externalRecipes.find((recipe) => recipe.name === param);

    let [allIngredients1, setAllIngredients1] = useState(curr1?.ingredients);
    let [newIngredient1, setNewIngredient1] = useState('');

    let [allInstructions1, setAllInstructions1] = useState(curr1?.instructions);
    let [newInstruction1, setNewInstruction1] = useState('');

    let [allIngredients2, setAllIngredients2] = useState(curr2?.ingredients);
    let [newIngredient2, setNewIngredient2] = useState('');

    let [allInstructions2, setAllInstructions2] = useState(curr2?.instructions);
    let [newInstruction2, setNewInstruction2] = useState('');

    return (
        <main className="recipe-page">
            <div className="intro">
                <img className="intro-image" src={curr1?.image2 || curr2?.image} alt={curr1?.name}/>
                {/* <img className="intro-image" src={curr2?.image} alt={curr2?.name}/> */}
                <h1 className="intro-title">{curr1?.name}</h1>
                <div className="intro-info">
                    {curr1?.info.map((i) => (<p>{i}</p>))}
                    <br></br>
                    {curr1?.intro}
                </div>
            </div>
            <div className="flex-container">
                <div className="flex-content">
                    <h2 className="label">Ingredients</h2>
                    <ul>
                        {allIngredients?.map((i) => (<li>{i}</li>))}
                    </ul>
                    <div className="add-values">
                        <p>Add ingredient: </p>
                        <input placeholder="2 cups of flour"
                            value={newIngredient}
                            onChange={(e: ChangeEvent<HTMLInputElement>) => {
                                setNewIngredient(e.target.value);}}
                        />
                        <button onClick={() => { 
                            if (allIngredients !== undefined) setAllIngredients([...allIngredients, newIngredient]);}}>Add Ingredient</button>
                    </div>
                    <h2 className="label">Instructions</h2>
                    <ol>
                        {allInstructions?.map((instr) => (<li>{instr}</li>))}
                    </ol>
                    <div className="add-values">
                        <p>Add an instruction: </p>
                        <input placeholder="Beat the eggs"
                            value={newInstruction}
                            onChange={(e: ChangeEvent<HTMLInputElement>) => {
                                setNewInstruction(e.target.value); }}
                        />
                        <button onClick={() => { if (allInstructions !== undefined) setAllInstructions([...allInstructions, newInstruction]);}}>Add Instruction</button>
                    </div>
                </div>
            </div>
        </main>
    );
}
